import{_ as e,o as t,c as o,a}from"./app-5b0f25a8.js";const n={},r=a('<h1 id="常用的meta标签" tabindex="-1"><a class="header-anchor" href="#常用的meta标签" aria-hidden="true">#</a> 常用的meta标签</h1><h2 id="_1-文档的字符编码" tabindex="-1"><a class="header-anchor" href="#_1-文档的字符编码" aria-hidden="true">#</a> 1.文档的字符编码</h2><p><code>&lt;meta charset=&quot;utf-8&quot;&gt;</code></p><h2 id="_2-移动端适配" tabindex="-1"><a class="header-anchor" href="#_2-移动端适配" aria-hidden="true">#</a> 2.移动端适配</h2><p><code>&lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0 minimal-ui&quot;&gt;</code></p><p>uc强制竖屏<br><code>&lt;meta name=&quot;screen-orientation&quot; content=&quot;portrait&quot;/&gt;</code></p><p>UC强制全屏<br><code>&lt;meta name=&quot;full-screen&quot; content=&quot;yes&quot;&gt;</code></p><h2 id="_3-seo优化" tabindex="-1"><a class="header-anchor" href="#_3-seo优化" aria-hidden="true">#</a> 3.SEO优化</h2><p>1、keywords用来告诉搜索引擎你网页的关键字是什么。<br><code>&lt;meta name=&quot;keywords&quot; content=&quot;xxxx&quot;&gt;</code></p><p>2、description用来告诉搜索引擎你的网站主要内容。<br><code>&lt;meta name=&quot;description&quot; content=&quot;xxx&quot;&gt;</code></p><h2 id="_4-强制浏览器使用webkit内核" tabindex="-1"><a class="header-anchor" href="#_4-强制浏览器使用webkit内核" aria-hidden="true">#</a> 4.强制浏览器使用WebKit内核</h2><p>强制Chromium内核，作用于360浏览器、QQ浏览器等国产双核浏览器：<br><code>&lt;meta name=&quot;render&quot; content=&quot;webkit&quot;/&gt;</code></p><p>强制Chromium内核，作用于其他双核浏览器：<br><code>&lt;meta name=&quot;force-rendering&quot; content=&quot;webkit&quot;/&gt;</code></p><p>如果有安装 Google Chrome Frame 插件则强制为Chromium内核，否则强制本机支持的最高版本IE内核，作用于IE浏览器：<br><code>&lt;meta http-equiv=&quot;X-UA-Compatible&quot; content=&quot;IE=Edge,chrome=1&quot;/&gt;</code></p><h2 id="_5-格式检测" tabindex="-1"><a class="header-anchor" href="#_5-格式检测" aria-hidden="true">#</a> 5.格式检测</h2><p><code>&lt;meta name=”format-detection” content=”telephone=no,email=no,adress=no”&gt;</code></p><ul><li>telephone，主要作用是是否设置自动将你的数字转化为拨号连接</li><li>email，告诉设备不识别邮箱，点击之后不自动发送</li><li>adress，跳转至地图</li></ul>',17),c=[r];function i(d,u){return t(),o("div",null,c)}const m=e(n,[["render",i],["__file","js-meta.html.vue"]]);export{m as default};
